%  Auto-generated by MATLAB on 30-Oct-2019 16:48:09


%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( x, y );

% Set up fittype and options.
ft = fittype( 'b+(p0/2)*erf((x-x0)/(sqrt(2)*w0))+a1*exp(-((x-x0)/(2*w0))^2)', 'independent', 'x', 'dependent', 'y' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
