opts.Robust = 'LAR';
opts.StartPoint = [0.5 0.716302635035789 0.48 0.8003 7];
opts.TolFun = 0.0001;
opts.TolX = 0.0001;
opts.Upper = [1 1 1 10 16];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
h = plot( fitresult, xData, yData );
legend('hide');
set(h, 'MarkerSize',30);


